INSERT INTO `rigte_now` (`id`, `title`, `text`, `img`) VALUES
	(5, 'ОШСКИЙ ГОСУДАРСТВЕННЫЙ УНИВЕРСИТЕТ УДОСТОИЛСЯ ВЫСОКОЙ ГОСУДАРСТВЕННОЙ НАГРАДЫ - ОРДЕНА“ДАНК”', '<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">В соответствии с Указом Президента Кыргызской Республики АлмазбекаАтамбаева от 30-августа 2017 года &laquo;О награждении государственными наградами Кыргызской Республики&raquo; за особый вклад в развитие социально-экономического, духовного и интеллектуального потенциала Ошский государственный университет награжден орденом &laquo;Данк&raquo;.</span></span></p>\r\n\r\n<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">Поздравляем коллектив Ошского государственного университета с высокой государственной наградой и желаем дальнейших успехов в плодотворной работе в воспитании подрастающего поколения, развитии образования, научных исследований, стабильности в стране и её процветания.</span></span></p>\r\n\r\n<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">С наградой, друзья! Пусть она послужит стимулом для творчества, поиска и обновлению каждому члену многотысячного профессорско-преподавательского коллектива.</span></span></p>\r\n\r\n<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">Ссылка на Указ президента КР:</span></span></p>\r\n\r\n<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">ibkntvt: http://kg.akipress.org/news:1402181/?from=portal&amp;place=nowread</span></span></p>\r\n', 'dank.jpg'),
	(6, 'ОШСКИЙ ГОСУДАРСТВЕННЫЙ УНИВЕРСИТЕТ УДОСТОИЛСЯ ВЫСОКОЙ ГОСУДАРСТВЕННОЙ НАГРАДЫ - ОРДЕНА“ДАНК”', '<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">В соответствии с Указом Президента Кыргызской Республики АлмазбекаАтамбаева от 30-августа 2017 года &laquo;О награждении государственными наградами Кыргызской Республики&raquo; за особый вклад в развитие социально-экономического, духовного и интеллектуального потенциала Ошский государственный университет награжден орденом &laquo;Данк&raquo;.</span></span></p>\r\n\r\n<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">Поздравляем коллектив Ошского государственного университета с высокой государственной наградой и желаем дальнейших успехов в плодотворной работе в воспитании подрастающего поколения, развитии образования, научных исследований, стабильности в стране и её процветания.</span></span></p>\r\n\r\n<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">С наградой, друзья! Пусть она послужит стимулом для творчества, поиска и обновлению каждому члену многотысячного профессорско-преподавательского коллектива.</span></span></p>\r\n\r\n<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">Ссылка на Указ президента КР:</span></span></p>\r\n\r\n<p><span style="font-size:16px"><span style="font-family:Times New Roman,Times,serif">ibkntvt: http://kg.akipress.org/news:1402181/?from=portal&amp;place=nowread</span></span></p>\r\n', 'dank.jpg');
